import React from 'react';
import { motion } from 'framer-motion';
import { PinContainer } from '../ui/3d-pin'; // Ensure to import PinContainer

// Define animation variants
const containerVariants = {
  visible: { opacity: 1, transition: { staggerChildren: 0.3 } },
  hidden: { opacity: 0 },
};

const TournamentCard = ({ name, date, teams }) => {
  return (
    <PinContainer title={name} href="#">
      <div className="flex basis-full flex-col p-4 tracking-tight text-slate-100/50 w-[20rem] h-[20rem]">s
        <h3 className="max-w-xs !pb-2 !m-0 font-bold text-base text-slate-100">
          {name}
        </h3>
        <div className="text-base !m-0 !p-0 font-normal">
          <span className="text-slate-500">{date}</span>
        </div>
        <div className="text-base !m-0 !p-0 font-normal">
          <span className="text-slate-500">{teams} Teams</span>
        </div>
        <div className="flex flex-1 w-full rounded-lg mt-4 bg-gradient-to-br from-violet-500 via-purple-500 to-blue-500" />
      </div>
    </PinContainer>
  );
};

const Tourna = () => {
  const tournaments = [
    {
      name: 'T20 Blast',
      date: 'June 15-30, 2023',
      teams: 8,
    },
    {
      name: 'ODI Championship',
      date: 'July 10-25, 2023',
      teams: 10,
    },
    {
      name: 'Test Series',
      date: 'August 5-30, 2023',
      teams: 4,
    },
  ];

  return (
    <section className="text-gray-600 body-font">
      <div className="container px-5 py-12 mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          className="flex flex-col text-center w-full"
        >
          <h2 className="font-semibold mb-4 text-center text-[#316685] text-2xl md:text-3xl lg:text-4xl" style={{ fontSize: '36px' }}>
            Featured Tournaments
          </h2>
          <p className="text-base sm:text-lg md:text-xl mb-8 leading-relaxed">
            Check out our upcoming cricket tournaments and join the excitement on the field!
          </p>
        </motion.div>
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate="visible"
          className="flex flex-wrap justify-center -m-4"
        >
          {tournaments.map((tournament, index) => (
            <motion.div key={index} className="p-4 md:w-1/2 lg:w-1/3 xl:w-1/4">
              <TournamentCard name={tournament.name} date={tournament.date} teams={tournament.teams} />
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Tourna;
