import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { useNavigate } from "react-router-dom";
import { ImagesSlider } from "../ui/images-slider"; // Import your ImagesSlider component

// Define animation variants
const containerVariants = {
  visible: { opacity: 1, transition: { staggerChildren: 0.5 } },
  hidden: { opacity: 0 },
};

const itemVariants = {
  visible: { opacity: 1, y: 0, transition: { type: 'spring', stiffness: 120 } },
  hidden: { opacity: 0, y: 20 },
};

const buttonVariants = {
  hover: { scale: 1.1, transition: { yoyo: Infinity, duration: 1.0 } }
};

// Import images from local folder
import image1 from '../../herosectionImages/image1.jpg';
import image2 from '../../herosectionImages/image2.jpg';
import image3 from '../../herosectionImages/image3.jpg';
import image4 from '../../herosectionImages/image4.jpg';
import image5 from '../../herosectionImages/image5.jpg';

const images = [image1, image2, image3, image4, image5];

const HeroSection = () => {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleNavigation = (path) => {
    setLoading(true);
    setTimeout(() => {
      navigate(path);
    }, 3000);
  };

  return (
    <motion.section
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className="relative text-gray-600 body-font flex items-center justify-center h-[85vh]"
    >
      {loading && (
        <div className="fixed inset-0 flex items-center justify-center bg-white z-50">
          <video
            src="https://cdnl.iconscout.com/lottie/premium/preview-watermark/cricket-ticket-animated-icon-download-in-lottie-json-gif-static-svg-file-formats--spectator-pass-event-match-pack-sports-games-icons-8805874.mp4"
            autoPlay
            loop
            muted
            className="h-40 w-40"
          />
        </div>
      )}

      <div className="absolute inset-0 bg-black bg-opacity-50"></div>
      
      <ImagesSlider className="absolute inset-0" images={images} />

      <div className="relative container mx-auto px-4 py-4 md:py-8 flex flex-col items-center text-center">
        <motion.div
          variants={itemVariants}
          className="flex flex-col items-center mb-16"
        >
          <h1 className="title-font text-2xl sm:text-3xl md:text-4xl lg:text-5xl mb-4 font-medium text-white">
            <span className="typewriter-first-line text-white">
              Experience the Thrill of Cricket
            </span>
            <br />
            <span className="typewriter-second-line text-white">
              with CRICKO
            </span>
          </h1>
          <div className="flex justify-center space-x-4">
            <motion.button
              whileHover="hover"
              variants={buttonVariants}
              onClick={() => handleNavigation("/about")}
              className="inline-flex text-white bg-custom-blue border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-custom-blue/80 rounded"
            >
              Explore Tournaments
            </motion.button>
            <motion.button
              whileHover="hover"
              variants={buttonVariants}
              onClick={() => handleNavigation("/contact")}
              className="inline-flex text-gray-700 bg-gray-100 border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-gray-200 rounded"
            >
              About Us
            </motion.button>
          </div>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default HeroSection;
