"use client";

import { cn } from "../lib/utils"; 
import React, { useEffect, useState } from "react";

export const InfiniteMovingCards = ({
  items,
  direction = "left",
  speed = "fast",
  pauseOnHover = true,
  className
}) => {
  const containerRef = React.useRef(null);
  const scrollerRef = React.useRef(null);

  const [start, setStart] = useState(false);

  useEffect(() => {
    if (containerRef.current && scrollerRef.current) {
      const scrollerContent = Array.from(scrollerRef.current.children);

      // Clone each item to create the repeating effect
      scrollerContent.forEach((item) => {
        const duplicatedItem = item.cloneNode(true);
        if (scrollerRef.current) {
          scrollerRef.current.appendChild(duplicatedItem);
        }
      });

      getDirection();
      getSpeed();
      setStart(true);
    }
  }, []);

  const getDirection = () => {
    if (containerRef.current) {
      containerRef.current.style.setProperty("--animation-direction", direction === "left" ? "forwards" : "reverse");
    }
  };

  const getSpeed = () => {
    if (containerRef.current) {
      const durationMap = {
        fast: "20s",
        normal: "40s",
        slow: "80s"
      };
      containerRef.current.style.setProperty("--animation-duration", durationMap[speed] || "20s");
    }
  };

  return (
    <div
      ref={containerRef}
      className={cn(
        "scroller relative z-20 max-w-7xl overflow-hidden [mask-image:linear-gradient(to_right,transparent,white_20%,white_80%,transparent)]",
        className
      )}
    >
      <ul
        ref={scrollerRef}
        className={cn(
          "flex min-w-full shrink-0 gap-4 py-4 w-max flex-nowrap",
          start && "animate-scroll ",
          pauseOnHover && "hover:[animation-play-state:paused]"
        )}
      >
        {items.map((item, idx) => (
          <li
            key={idx}
            className="w-[200px] h-[300px] max-w-full relative rounded-lg border border-b-0 flex-shrink-0 border-slate-700 px-4 py-3 md:w-[250px] md:h-[350px]"
            style={{
              backgroundColor: "#316685", // Set card background color to #316685
            }}
          >
            <img
              src={item.image}
              alt={item.title}
              className="w-full h-[80px] md:h-[100px] object-cover rounded-md mb-2"
            />
            <h2 className="text-sm md:text-base font-semibold text-gray-100">{item.title}</h2>
            <p className="text-xs md:text-sm text-gray-300 mb-1">{item.description}</p>
            <a
              href={item.link}
              className="text-xs md:text-sm hover:underline"
              style={{ color: "#f5b921" }} // Change "Read more" link color to #f5b921
              target="_blank"
              rel="noopener noreferrer"
            >
              Read more
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
};
