import React, { useState } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

export const PinContainer = ({ children, title, className, containerClassName, href }) => {
  const [transform, setTransform] = useState("rotateX(0deg) scale(1)");

  const onMouseEnter = () => {
    setTransform("rotateX(40deg) scale(0.8)");
  };

  const onMouseLeave = () => {
    setTransform("rotateX(0deg) scale(1)");
  };

  return (
    <Link to={href} className={`relative group/pin z-50 cursor-pointer ${containerClassName}`}>
      <div
        style={{
          perspective: "1000px",
        }}
        className="flex items-center justify-center w-full h-full"
        onMouseEnter={onMouseEnter}
        onMouseLeave={onMouseLeave}
      >
        <motion.div
          style={{
            transform: transform,
          }}
          className="flex items-center justify-center p-4 rounded-2xl shadow-lg bg-[#1f5156] border border-white/[0.1] group-hover/pin:border-white/[0.2] transition duration-700"
        >
          <div className={`relative z-50 ${className}`}>{children}</div>
        </motion.div>
      </div>
      <PinPerspective title={title} />
    </Link>
  );
};

export const PinPerspective = ({ title }) => {
  return (
    <motion.div className="pointer-events-none w-96 h-80 flex items-center justify-center opacity-0 group-hover/pin:opacity-100 z-[60] transition duration-500">
      <div className="absolute inset-0 flex justify-center items-center">
        <div className="flex space-x-2 items-center z-10 rounded-full bg-zinc-950 py-0.5 px-4 ring-1 ring-white/10">
          <span className="relative z-20 text-white text-xs font-bold inline-block py-0.5">
            {title}
          </span>
          <span className="absolute -bottom-0 left-[1.125rem] h-px w-[calc(100%-2.25rem)] bg-gradient-to-r from-emerald-400/0 via-emerald-400/90 to-emerald-400/0 transition-opacity duration-500"></span>
        </div>

        {/* Circular animated elements */}
        <div className="relative">
          {Array.from({ length: 3 }).map((_, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, scale: 0, x: "-50%", y: "-50%" }}
              animate={{ opacity: [0, 1, 0.5, 0], scale: 1 }}
              transition={{ duration: 6, repeat: Infinity, delay: index * 2 }}
              className="absolute left-1/2 top-1/2 h-[11.25rem] w-[11.25rem] rounded-full bg-sky-500/[0.08] shadow-[0_8px_16px_rgb(0_0_0/0.4)]"
              style={{ transform: "translate(-50%, -50%)" }}
            />
          ))}
        </div>
      </div>
    </motion.div>
  );
};
