import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const TeamFormPage1 = () => {
  const [formData, setFormData] = useState({
    tname: "",
    taddress: "",
    captname: "",
    phone: "",
    logo: ""
  });

  const [loading, setLoading] = useState(false);
  const [isVisible, setIsVisible] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsVisible(true);
    }, 100); // Delay for fade-in effect
    return () => clearTimeout(timer);
  }, []);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData({
          ...formData,
          logo: reader.result
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    localStorage.setItem("teamFormData", JSON.stringify(formData));
    setTimeout(() => {
      setLoading(false);
      navigate("/register/page2");
    }, 3000);
  };

  return (
    <div className="container mx-auto p-4">
      {loading && (
        <div className="fixed inset-0 flex items-center justify-center bg-white z-50">
          <video
            src="https://cdnl.iconscout.com/lottie/premium/preview-watermark/cricket-ticket-animated-icon-download-in-lottie-json-gif-static-svg-file-formats--spectator-pass-event-match-pack-sports-games-icons-8805874.mp4"
            autoPlay
            loop
            muted
            className="h-40 w-40"
          />
        </div>
      )}
      <div
        className={`max-w-md mx-auto border border-gray-300 rounded-lg p-4 shadow-md transition-opacity duration-500 ${isVisible ? 'opacity-100 translate-y-0' : 'opacity-0 translate-y-4'}`}
      >
        <h2 className="text-xl font-semibold mb-4 text-center" style={{ fontSize: "36px" }}>
          Register Your Team
        </h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {["tname", "taddress", "captname", "phone"].map((field) => (
            <div key={field}>
              <label className="block text-sm font-medium text-gray-700">{field.charAt(0).toUpperCase() + field.slice(1).replace('name', "'s Name").replace('address', 'Address')}</label>
              <input
                type="text"
                name={field}
                value={formData[field]}
                onChange={handleChange}
                className="mt-1 block w-full p-2 border border-gray-300 rounded-md text-sm"
                required
              />
            </div>
          ))}
          <div>
            <label className="block text-sm font-medium text-gray-700">Team Logo</label>
            <input
              type="file"
              name="logo"
              accept="image/*"
              onChange={handleFileChange}
              className="mt-1 block w-full p-2 border border-gray-300 rounded-md text-sm"
            />
          </div>
          <button
            type="submit"
            className="bg-[#1f5156] text-white px-20 py-4 rounded-md transition-transform duration-300 ease-in-out transform hover:scale-105"
          >
            Next
          </button>
        </form>
      </div>
    </div>
  );
};

export default TeamFormPage1;
