import React, { useState } from "react";
import { motion } from "framer-motion";
import { Link } from "react-router-dom";

// Define animation variants
const containerVariants = {
  visible: { opacity: 1, transition: { staggerChildren: 0.5 } },
  hidden: { opacity: 0 },
};

const itemVariants = {
  visible: { opacity: 1, y: 0, transition: { type: 'spring', stiffness: 120 } },
  hidden: { opacity: 0, y: 20 },
};

const buttonVariants = {
  hover: { scale: 1.1, transition: { yoyo: Infinity, duration: 1.0 } }
};

// Import images from local folder
import image1 from '../../herosectionImages/image1.jpg';
import image2 from '../../herosectionImages/image2.jpg';
import image3 from '../../herosectionImages/image3.jpg';
import image4 from '../../herosectionImages/image4.jpg';
import image5 from '../../herosectionImages/image5.jpg';

const images = [image1, image2, image3, image4, image5];

const HeroSection = () => {
  const [currentImage, setCurrentImage] = useState(images[0]);
  const [hovering, setHovering] = useState(false);

  // Handle hover effect to change background image
  const handleMouseEnter = () => {
    setHovering(true);
  };

  const handleMouseLeave = () => {
    setHovering(false);
  };

  // If hovering, cycle through images
  if (hovering) {
    setTimeout(() => {
      setCurrentImage((prevImage) => {
        const currentIndex = images.indexOf(prevImage);
        return images[(currentIndex + 1) % images.length]; // Cycle through images
      });
    }, 2000); // Change image every 1 second
  }

  return (
    <motion.section
      variants={containerVariants}
      initial="hidden"
      animate="visible"
      className="relative text-gray-600 body-font flex items-center justify-center bg-cover bg-bottom h-[85vh] md:h-[85vh] lg:h-[85vh] xl:h-[85vh]"
      style={{
        backgroundImage: `url(${currentImage})`, // Dynamic background image
        backgroundSize: 'cover',
        backgroundPosition: '100% 0', // Start image from the right
        transition: 'background-position-x 0.5s ease-in-out', // Animate from right
      }}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <div className="absolute inset-0 bg-black bg-opacity-50"></div>
      <div className="relative container mx-auto px-4 py-4 md:py-8 flex flex-col items-center text-center">
        <motion.div
          variants={itemVariants}
          className="flex flex-col items-center mb-16"
        >
          <h1
            className="title-font text-2xl sm:text-3xl md:text-4xl lg:text-5xl mb-4 font-medium text-white"
            style={{ fontFamily: '"Arial Rounded MT Bold", Arial, sans-serif' }}
          >
            <div
              className="typewriter-first-line text-white"
              style={{
                display: "inline-block",
                whiteSpace: "nowrap",
                overflow: "hidden",
              }}
            >
              Experience the Thrill of Cricket
            </div>
            <br className="hidden lg:inline-block" />
            <div
              className="typewriter-second-line text-white"
              style={{
                display: "inline-block",
                whiteSpace: "nowrap",
                overflow: "hidden",
              }}
            >
              with CRICKO
            </div>
          </h1>
          <p className="text-base sm:text-lg md:text-xl mb-8 leading-relaxed text-white">
            Join us for exciting cricket tournaments in various formats. Whether you're a player or a fan, we've got something for everyone. Discover the passion, skill, and excitement of cricket with CRICKO Organization.
          </p>
          <div className="flex justify-center space-x-4">
            <Link to="/about">
              <motion.button
                whileHover="hover"
                variants={buttonVariants}
                className="inline-flex text-white bg-custom-blue border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-custom-blue/80 rounded"
              >
                Explore Tournaments
              </motion.button>
            </Link>
            <Link to="/contact">
              <motion.button
                whileHover="hover"
                variants={buttonVariants}
                className="inline-flex text-gray-700 bg-gray-100 border-0 py-2 px-6 text-base md:text-lg focus:outline-none hover:bg-gray-200 rounded"
              >
                About Us
              </motion.button>
            </Link>
          </div>
        </motion.div>
      </div>
    </motion.section>
  );
};

export default HeroSection;
